import React from 'react';
import Todos from './components/Todos';
import AddTodo from './components/AddTodo';
import Header from './components/layout/Header';

import './App.css';

class App extends React.Component {
  state = {
    todos: [
      {
        id: 1,
        title: 'Finish React JS Crash Course by Traversy',
        completed: false
      },
      {
        id: 2,
        title: 'Make a weather toolbar for Deer Web app using React',
        completed: false
      },
      {
        id: 3,
        title: 'Commit Todo to my github repository',
        completed: false
      }
    ]
  }

  markComplete = (id) => {
    this.setState({ todos: this.state.todos.map(todo => {
      if(todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    }) });
  }

  deleteItem = (id) => {
    this.setState({ todos: [...this.state.todos.filter((todo) => todo.id !== id)]
    });
  }

  render() {

    console.log("State of App component: " + this.state.todos);
    return (
      <div className="App">
        <Header />
        <AddTodo />
        <Todos todos={this.state.todos} markComplete={this.markComplete} deleteItem={this.deleteItem}/>
      </div>
    )
  };
}

export default App;
