import React from 'react';
import { Link } from 'react-router-dom';

function Header() {
    return (
        <header>
            <h1 style={headerStyle}>ToDoList</h1>
            <Link style={linkStyle} to="/">Home</Link> | <Link style={linkStyle} to="/about">About</Link>
        </header>
    );
};

const linkStyle = {
    color: 'white',
    textDecoration: 'none'
}

const headerStyle = {
    background: 'darkgrey',
    color: 'white',
    textAlign: 'center',
    padding: '10px'
}
export default Header;
