import React from 'react';
import Todos from './components/Todos';
import './App.css';

class App extends React.Component {
  state = {
    todos: [
      {
        id: 1,
        title: 'Finish React JS Crash Course by Traversy',
        completed: false
      },
      {
        id: 2,
        title: 'Make a weather toolbar for Deer Web app using React',
        completed: false
      },
      {
        id: 3,
        title: 'Commit Todo to my github repository',
        completed: false
      }
    ]
  }
  markComplete = (id) => {
    console.log(this.props);
    this.props.todo.completed = true;
  }

  render() {
    const { id, title} = this.props.todo;
    console.log("State of App component: " + this.state.todos);
    return (
      <div className="App">
        <h1>ToDo App</h1>
        <Todos todos={this.state.todos} markComplete={this.markComplete.bind(this, id)}/>
      </div>
    )
  };
}

export default App;
